export default async function () {
  const startTime = Date.parse($dt_timeframe_from).toString();  // Get Dashboard Start Time
  const endTime = Date.parse($dt_timeframe_to).toString();      // Get Dashboard End Time
  var appsList = "\""+$AppName[0]+"\"";
  let i = 1;
  // Build Apps List
  while (i < 10) {
    if ($AppName[i]==null) break;
    appsList = appsList+",\""+$AppName[i]+"\"";
    i++;
  }
  var kuaList = "\""+$KeyUserAction[0].replace(/"/g, "\"\"")+"\"";
  let k = 1;
  // Build KUA List
  while (k < 10) {
    if ($KeyUserAction[k]==null) break;
    kuaList = kuaList+",\""+$KeyUserAction[k].replace(/"/g, "\"\"")+"\"";
    k++;
  }
  // Calculate Chart Interval
  const timeDiff = (endTime-startTime)/3600/1000/24;
  var chartInterval = "1m"
  if (timeDiff>.5)
    var chartInterval = "10m"
  if (timeDiff>1)
    var chartInterval = "1h"
  const usqlQuery = 'SELECT DATETIME(endTime,  "MM-dd HH:mm:ss","'+chartInterval+'") AS "Date", PERCENTILE('+$KPI+',50) AS "50th Percentile", PERCENTILE('+$KPI+',90) AS "90th Percentile", PERCENTILE('+$KPI+',95) AS "95th Percentile", PERCENTILE('+$KPI+',99) AS "99th Percentile" from useraction WHERE useraction.application in ('+appsList+') and useraction.name in ('+kuaList+') and starttime>='+startTime+' and endtime<='+endTime+' GROUP BY  DATETIME(endTime,  "MM-dd HH:mm:ss","'+chartInterval+'") limit 5000';
  const response = await rumUserSessionsClient.getUsqlResultAsTable({query: usqlQuery, startTimestamp: startTime, endTimestamp: endTime});
  const sampleCount = response.values.length;
  return {
    records: new Array(sampleCount).fill(null).map((_, index, array) => {
      return {
        Date: response.values[index][0],
        '50th Percentile': (response.values[index][2]/1000).toFixed(2),
        '90th Percentile': (response.values[index][3]/1000).toFixed(2),
        '95th Percentile': (response.values[index][4]/1000).toFixed(2),
        '99th Percentile': (response.values[index][5]/1000).toFixed(2)
      };
    }),
    types: [
      {
        indexRange: [0, sampleCount],
        mappings: {
         Date: { type: 'timestamp' },
          '50th Percentile': { type: 'double' },
          '90th Percentile': { type: 'double' },
          '95th Percentile': { type: 'double' },
          '99th Percentile': { type: 'double' }
        },
      },
    ],
  };
}

