import { rumUserSessionsClient } from "@dynatrace-sdk/client-classic-environment-v1";


export default async function () {
  const startTime = Date.parse($dt_timeframe_from).toString();  // Get Dashboard Start Time
  const endTime = Date.parse($dt_timeframe_to).toString();      // Get Dashboard End Time
  const applicationName = $AppName;
  const ConversionName = $ConversionGoal;
  const revenueName = $Revenue;
  // Calculate Chart Interval
  const timeDiff = (endTime-startTime)/3600/1000/24;
  var chartInterval = "1m"
  if (timeDiff>.5)
    var chartInterval = "10m"
  if (timeDiff>1)
    var chartInterval = "1h"
  const usqlQuery = 'SELECT DISTINCT DATETIME(endTime,  "MM-dd HH:mm:ss","'+chartInterval+'") AS "Date", CONDITION(sum(usersession.doubleproperties.'+$Revenue+'), where useraction.matchingconversiongoals="'+$ConversionGoal+'")  AS "Revenue", CONDITION(sum(usersession.doubleproperties.'+$Revenue+'), where not useraction.matchingconversiongoals="'+$ConversionGoal+'") AS "Lost Revenue" from usersession WHERE useraction.application="'+$AppName+'" and starttime>='+startTime+' and endtime<='+endTime+' limit 5000';
  const response = await rumUserSessionsClient.getUsqlResultAsTable({query: usqlQuery, startTimestamp: startTime, endTimestamp: endTime});
  const sampleCount = response.values.length;

  return {
    records: new Array(sampleCount).fill(null).map((_, index, array) => {

      return {
        Date: response.values[index][0],
        'Revenue': response.values[index][1],
        'Lost Revenue': response.values[index][2]         
      };
    }),
    types: [
      {
        indexRange: [0, sampleCount],
        mappings: {

         Date: { type: 'timestamp' },
          'Revenue': { type: 'double' },
          'Lost Revenue': { type: 'double' }
        },
      },
    ],
  };
}

